function disablemodel(mdl) for key, value in pairs(mdl) do value.setEnabled(false) end end  disablemodel(armor_model) model.MIMIC_TORSO.setScale({1,1,1}) model.MIMIC_TORSO.setPos({0,12,0}) model.MIMIC_TORSO.bone.cheese.setPivot({0,12,0}) rotamount = 0 lastspeed = 0 velocity = {0,0,0} lastpos = {0,0,0} function tick() velocity = {player.getPos()[1] - lastpos[1], player.getPos()[2] - lastpos[2], player.getPos()[3] - lastpos[3]} lastpos = player.getPos() speed = math.sqrt(math.pow(velocity[1],2) + math.pow(velocity[3],2))*(50) lastrotamount = rotamount rotamount = rotamount + speed end function render(delta)  disspeed = lerp(lastrotamount, rotamount, delta) if speed ~= 0 then model.MIMIC_TORSO.bone.setRot({0, 0, 90}) model.MIMIC_TORSO.bone.cheese.setRot({0, disspeed, 0}) model.MIMIC_TORSO.setPos({-8,4,0}) else model.MIMIC_TORSO.bone.cheese.setRot({0, 0, 0}) model.MIMIC_TORSO.bone.setRot({0, 0, 0}) model.MIMIC_TORSO.setPos({0,12,0}) end end function lerp(a, b, t) return a + (b - a) * t end